export default function FeaturesBlocks() {
  return (
    <section className="relative" id="spotlight">
      {/* Main content container */}
      <div className="max-w-screen-xl mx-auto px-6">
        {/* Responsive vertical padding container */}
        <div className="py-12 md:py-24">
          {/* Section header */}
          <Header
            heading="Built on a foundation of fast, production-grade tooling"
            headingLevel={2}
            subtext="Powered By..."
          />
          {/* Section content */}
          <Mapper<FeatureCardProps>
            data={featureCards}
            renderItem={(card) => (
              <li key={card.id}>
                <Link
                  href={card.url}
                  rel="noopener noreferrer"
                  target="_blank"
                  title={`Visit the ${card.title} website`}
                  className="
                  p-6
                  border
                  border-solid
                  rounded-lg
                  bg-gradient-to-b from-gray-900 to-gray-800
                  transition
                  duration-300
                  ease-in-out
                  bg-gray-900 
                  border-gray-800
                  hover:border-gray-600
                  hover:bg-gradient-to-b hover:from-gray-800 hover:to-gray-700
                  flex-col
                  flex
                  gap-4
                "
                >
                  {card.icon}
                  <h3 className="font-semibold sm:text-lg text-base text-white">
                    {card.title}
                  </h3>
                  <p className="text-base text-gray-400">{card.description}</p>
                </Link>
              </li>
            )}
            WrapperElement="ul"
            wrapperProps={{
              className: 'grid gap-6 md:grid-cols-2 lg:grid-cols-3',
              'data-aos': 'fade-up',
            }}
          />
        </div>
      </div>
    </section>
  )
}
